mapper.update:
UPDATE Anime a
SET review_count = a.review_count + 1
WHERE anime_id = #{animeId}

UPDATE Anime a
SET review_count = a.review_count - 1
WHERE anime_id = #{animeId}

UPDATE Anime a
SET review_average_score = #{reviewAverageScore}
WHERE anime_id = #{animeId}

UPDATE Anime a JOIN ( SELECT r.anime_id AS animeId, AVG (r.rating) AS avgRating FROM Review r WHERE r.anime_id IN
#{id}
GROUP BY r.anime_id ) agg ON agg.animeId = a.anime_id SET a.review_average_score = agg.avgRating
