mapper.select:
SELECT COUNT(*) FROM Anime a WHERE
a.is_adult = #{includeAdult} AND
a.status = 'NOT_YET_RELEASED' AND a.cover_image_url != #{defaultCoverUrl}

SELECT a.anime_id AS animeId, a.title_kor AS titleKor, a.title_english AS titleEng, a.title_romaj AS titleRom, a.title_native AS titleNat, a.cover_image_url AS coverImageUrl, a.start_date AS startDate, a.format AS format, a.is_adult AS isAdult FROM Anime a WHERE
a.is_adult = #{includeAdult} AND
a.status = 'NOT_YET_RELEASED' AND a.cover_image_url != #{defaultCoverUrl}
AND a.anime_id < #{lastId}
ORDER BY ${orderByQuery} LIMIT #{size}

SELECT a.anime_id AS animeId, a.title_kor AS titleKor, a.title_english AS titleEng, a.title_romaj AS titleRom, a.title_native AS titleNat, a.cover_image_url AS coverImageUrl, a.start_date AS startDate, a.format AS format, pao.score AS score, a.is_adult AS isAdult FROM Anime a JOIN PopularityAnimeOrder pao ON pao.anime_id = a.anime_id WHERE
a.is_adult = #{includeAdult} AND
a.status = 'NOT_YET_RELEASED' AND a.cover_image_url != #{defaultCoverUrl}
AND pao.score < #{lastId}
ORDER BY ${orderByQuery} LIMIT #{size}

SELECT a.anime_id AS animeId, a.title_kor AS titleKor, a.title_english AS titleEng, a.title_romaj AS titleRom, a.title_native AS titleNat, a.cover_image_url AS coverImageUrl, a.start_date AS startDate, a.format AS format, a.is_adult AS isAdult FROM Anime a WHERE
a.is_adult = #{includeAdult} AND
a.status = 'NOT_YET_RELEASED' AND a.cover_image_url != #{defaultCoverUrl}
AND ( a.start_date > #{lastValue} OR (a.start_date = #{lastValue} AND a.anime_id > #{lastId}) OR a.start_date IS NULL )
AND a.anime_id > #{lastId} AND a.start_date IS NULL
ORDER BY a.start_date IS NULL ASC, ${orderByQuery}, a.anime_id LIMIT #{size}

SELECT ra.series_group_id
FROM Relatedanime ra
WHERE ra.anime_id = #{animeId}

SELECT a.anime_id AS animeId, a.title_kor AS titleKor, a.title_english AS titleEng, a.title_romaj AS titleRom, a.title_native AS titleNat, a.cover_image_url AS coverImageUrl FROM anime a WHERE a.anime_id IN (SELECT ara.anime_id FROM anilistrecommendationsanimes ara WHERE ara.recommendation_anime_id = #{animeId})
AND a.anime_id < #{lastId}
ORDER BY a.anime_id DESC LIMIT #{size}

SELECT a.anime_id AS animeId,
       a.title_kor AS titleKor,
       a.title_english AS titleEng,
       a.title_romaj AS titleRom,
       a.title_native AS titleNat,
       a.cover_image_url AS coverImageUrl,
       a.start_date AS startDate
FROM anime a
WHERE anime_id IN (SELECT ra.anime_id
                   FROM relatedanime ra
                   WHERE ra.series_group_id = #{seriesGroupId}
                   AND ra.anime_id != #{refAnimeId}
                   )

    AND a.anime_id > #{lastId}

ORDER BY a.anime_id
LIMIT #{size}
