mapper.select:
SELECT COUNT(*) FROM Anime a WHERE
a.is_adult = #{includeAdult} AND
a.status = 'NOT_YET_RELEASED' AND a.cover_image_url != #{defaultCoverUrl}

SELECT a.anime_id AS animeId,
       a.title_kor AS title,
       a.cover_image_url AS coverImageUrl,
       a.start_date AS startDate,
       a.format AS format,
       a.is_adult AS isAdult
FROM Anime a
WHERE

    a.is_adult = #{includeAdult}
    AND

    a.status = 'NOT_YET_RELEASED'
AND a.cover_image_url != #{defaultCoverUrl}

    AND a.anime_id < #{lastId}

ORDER BY ${orderByQuery}
LIMIT #{size}

SELECT a.anime_id AS animeId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, a.start_date AS startDate, a.format AS format, pao.popularity_anime_order_id AS popularId, a.is_adult AS isAdult FROM Anime a JOIN PopularityAnimeOrder pao ON pao.anime_id = a.anime_id WHERE
a.is_adult = #{includeAdult} AND
a.status = 'NOT_YET_RELEASED' AND a.cover_image_url != #{defaultCoverUrl}
AND pao.popularity_anime_order_id > #{lastId}
ORDER BY ${orderByQuery} LIMIT #{size}

SELECT a.anime_id AS animeId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, a.start_date AS startDate, a.format AS format, a.is_adult AS isAdult FROM Anime a WHERE
a.is_adult = #{includeAdult} AND
a.status = 'NOT_YET_RELEASED' AND a.cover_image_url != #{defaultCoverUrl}
AND ( a.start_date > #{lastValue} OR (a.start_date = #{lastValue} AND a.anime_id > #{lastId}) )
ORDER BY a.start_date IS NULL ASC, ${orderByQuery}, a.anime_id LIMIT #{size}
