mapper.select:
SELECT ag.anime_id AS animeId, g.genre_kor AS genre FROM animegenres ag JOIN genres g ON ag.genre_id = g.genre_id WHERE ag.anime_id IN
#{id}
ORDER BY ag.anime_id, g.genre_kor

SELECT COUNT(*)
FROM popularityanimeorder pa
    JOIN anime a
        ON pa.anime_id = a.anime_id

    pa.created_at = #{nowString}


        AND a.title_search LIKE CONCAT('%', #{query}, '%')




         AND a.start_date BETWEEN #{startDate} AND #{endDate}




        AND EXISTS (
            SELECT 1
            FROM animegenres ag2
            WHERE ag2.anime_id = a.anime_id
            AND ag2.genre_id = #{genres}
        )

SELECT pa.score AS score,
       a.anime_id AS animeId,
       a.title_kor AS title,
       a.cover_image_url AS coverImageUrl
FROM popularityanimeorder pa
   JOIN anime a
       ON pa.anime_id = a.anime_id

    pa.created_at = #{nowString}


        AND a.title_search LIKE CONCAT('%', #{query}, '%')




        AND a.start_date BETWEEN #{startDate} AND #{endDate}




        AND EXISTS (
            SELECT 1
            FROM animegenres ag2
            WHERE ag2.anime_id = a.anime_id
            AND ag2.genre_id = #{genres}
        )




        AND pa.score < #{lastId}



ORDER BY pa.score DESC
LIMIT #{size}
