mapper.select:
SELECT COUNT(*)
FROM UserAnimeStatus AS uas
WHERE uas.status = #{animeStatus}
    AND
      uas.user_id = #{userId}

SELECT a.anime_id AS animeId, uas.user_anime_status_id AS userAnimeStatusId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, uas.created_at AS createdAt FROM anime AS a JOIN useranimestatus AS uas ON a.anime_id = uas.anime_id WHERE uas.status = #{status} AND uas.user_id = #{userId}
AND ( uas.created_at < #{lastCreatedAt} OR (uas.created_at = #{lastCreatedAt} AND uas.user_anime_status_id < #{lastId}) )
ORDER BY uas.created_at DESC, uas.user_anime_status_id DESC LIMIT #{size}

SELECT a.anime_id AS animeId, uas.user_anime_status_id AS userAnimeStatusId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, uas.created_at AS createdAt FROM anime AS a JOIN useranimestatus AS uas ON a.anime_id = uas.anime_id WHERE uas.status = #{status} AND uas.user_id = #{userId}
AND ( uas.created_at < #{lastCreatedAt} OR (uas.created_at = #{lastCreatedAt} AND uas.user_anime_status_id < #{lastId}) )
ORDER BY uas.created_at DESC, uas.user_anime_status_id DESC LIMIT #{size}

SELECT a.anime_id AS animeId, uas.user_anime_status_id AS userAnimeStatusId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, r.rating AS myRating, uas.created_at AS createdAt FROM anime AS a JOIN useranimestatus AS uas ON a.anime_id = uas.anime_id LEFT JOIN review AS r ON a.anime_id = r.anime_id AND r.user_id = uas.user_id WHERE uas.status = #{status} AND uas.user_id = #{userId}
AND ( uas.created_at < #{lastCreatedAt} OR (uas.created_at = #{lastCreatedAt} AND uas.user_anime_status_id < #{lastId}) )
ORDER BY uas.created_at DESC, uas.user_anime_status_id DESC LIMIT #{size}
