mapper.select:
SELECT COUNT(*)
FROM UserAnimeStatus AS uas
WHERE uas.status = #{animeStatus}
    AND
      uas.user_id = #{userId}

SELECT ual.anime_id AS animeId, ual.anime_like_id AS animeLikeId, a.title_kor AS titleKor, a.title_english AS titleEng, a.title_romaj AS titleRom, a.title_native AS titleNat, a.cover_image_url AS coverImageUrl FROM UserAnimeLike AS ual JOIN Anime AS a ON ual.anime_id = a.anime_id WHERE ual.user_id = #{userId}
AND ual.anime_like_id < #{lastId}
ORDER BY ual.anime_like_id DESC LIMIT #{size}

SELECT ulva.voiceactor_id AS personId, ulva.user_liked_voiceactor_id AS userLikedVoiceActorId, va.name_kor AS name, va.profile_image AS profileImageUrl FROM UserLikedVoiceActor AS ulva JOIN VoiceActor AS va ON ulva.voiceactor_id = va.voiceactor_id WHERE ulva.user_id = #{userId}
AND ulva.user_liked_voiceactor_id < #{lastId}
ORDER BY ulva.user_liked_voiceactor_id DESC LIMIT #{size}

SELECT a.anime_id AS animeId, uas.user_anime_status_id AS userAnimeStatusId, a.title_kor AS titleKor, a.title_english AS titleEng, a.title_romaj AS titleRom, a.title_native AS titleNat, a.cover_image_url AS coverImageUrl FROM anime AS a JOIN useranimestatus AS uas ON a.anime_id = uas.anime_id WHERE uas.status = #{status} AND uas.user_id = #{userId}
AND uas.user_anime_status_id < #{lastId}
ORDER BY uas.user_anime_status_id DESC LIMIT #{size}

SELECT a.anime_id AS animeId, uas.user_anime_status_id AS userAnimeStatusId, a.title_kor AS titleKor, a.title_english AS titleEng, a.title_romaj AS titleRom, a.title_native AS titleNat, a.cover_image_url AS coverImageUrl FROM anime AS a JOIN useranimestatus AS uas ON a.anime_id = uas.anime_id WHERE uas.status = #{status} AND uas.user_id = #{userId}
AND uas.user_anime_status_id < #{lastId}
ORDER BY uas.user_anime_status_id DESC LIMIT #{size}

SELECT a.anime_id AS animeId, uas.user_anime_status_id AS userAnimeStatusId, a.title_kor AS titleKor, a.title_english AS titleEng, a.title_romaj AS titleRom, a.title_native AS titleNat, a.cover_image_url AS coverImageUrl, r.rating AS myRating FROM anime AS a JOIN useranimestatus AS uas ON a.anime_id = uas.anime_id LEFT JOIN review AS r ON a.anime_id = r.anime_id AND r.user_id = uas.user_id WHERE uas.status = #{status} AND uas.user_id = #{userId}
AND uas.user_anime_status_id < #{lastId}
ORDER BY uas.user_anime_status_id DESC LIMIT #{size}

SELECT COUNT(*)
FROM review AS r
WHERE r.user_id = #{userId}

SELECT
    a.anime_id AS animeId,
    a.title_kor AS titleKor,
    a.title_english AS titleEng,
    a.title_romaj AS titleRom,
    a.title_native AS titleNat,
    a.cover_image_url AS coverImageUrl,
    r.rating AS rating,
    r.review_id AS reviewId,
    r.content AS reviewContent,
    DATE_FORMAT(r.created_at, '%Y. %m. %d') AS createdAt,
    r.like_count AS likeCount,
    EXISTS(
        SELECT 1
        FROM reviewlike AS rl
        WHERE
                rl.user_id = #{userId}
            AND
                rl.review_id = r.review_id
    ) AS isLiked
FROM anime AS a
JOIN review AS r ON a.anime_id = r.anime_id

    r.user_id = #{userId}



            AND r.review_id < #{lastId}


            AND (
                r.like_count < #{lastLikeCount}
                OR (r.like_count = #{lastLikeCount}) AND r.review_id < #{lastId}
            )


            AND (
                r.rating > #{lastRating}
                OR (r.rating = #{lastRating}) AND r.review_id < #{lastId}
            )


            AND (
                r.rating < #{lastRating}
                OR (r.rating = #{lastRating}) AND r.review_id < #{lastId}
            )






        ORDER BY r.review_id DESC


        ORDER BY r.like_count DESC, r.review_id DESC


        ORDER BY r.rating ASC, r.review_id DESC


        ORDER BY r.rating DESC, r.review_id DESC


        ORDER BY r.review_id DESC



LIMIT #{size}

SELECT
    a.anime_id AS animeId,
    a.title_kor AS titleKor,
    a.title_english AS titleEng,
    a.title_romaj AS titleRom,
    a.title_native AS titleNat,
    a.cover_image_url AS coverImageUrl,
    r.rating AS rating,
    r.review_id AS reviewId,
    DATE_FORMAT(r.created_at, '%Y. %m. %d') AS createdAt
FROM anime AS a
JOIN review AS r ON a.anime_id = r.anime_id

    r.user_id = #{userId}



            AND r.review_id < #{lastId}


            AND (
                r.like_count < #{lastLikeCount}
                OR (r.like_count = #{lastLikeCount}) AND r.review_id < #{lastId}
            )


            AND (
                r.rating > #{lastRating}
                OR (r.rating = #{lastRating}) AND r.review_id < #{lastId}
            )


            AND (
                r.rating < #{lastRating}
                OR (r.rating = #{lastRating}) AND r.review_id < #{lastId}
            )






        ORDER BY r.review_id DESC


        ORDER BY r.like_count DESC, r.review_id DESC


        ORDER BY r.rating ASC, r.review_id DESC


        ORDER BY r.rating DESC, r.review_id DESC


        ORDER BY r.review_id DESC



LIMIT #{size}

SELECT COUNT(*)
FROM useranimelike AS ual
