mapper.select:
SELECT a.anime_id AS animeId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, ar.rank_num AS rankNum FROM Anime AS a JOIN AnimeRanking AS ar ON a.anime_id = ar.anime_id WHERE 1 = 1
AND ar.year = #{year}
AND ar.season_int = #{season}
AND ar.rank_type = 'year_season' AND a.anime_id IN ( SELECT DISTINCT(ar2.anime_id) FROM AnimeRanking AS ar2 JOIN AnimeGenres AS ag ON ar2.anime_id = ag.anime_id JOIN Genres AS g ON ag.genre_id = g.genre_id
WHERE g.genre_kor = #{genre}
) AND DATE(ar.rank_date) = #{rankDate} ORDER BY ar.rank_num

SELECT
    a.anime_id AS animeId,
    a.title_kor AS title,
    a.cover_image_url AS coverImageUrl,
    ar.rank_num AS rankNum
FROM Anime AS a
JOIN AnimeRanking AS ar ON a.anime_id = ar.anime_id
WHERE 1 = 1

    AND ar.year = #{year}


    AND ar.season_int = #{season}

    AND ar.rank_type = 'year_season'
    AND a.anime_id IN (
        SELECT DISTINCT(ar2.anime_id)
        FROM AnimeRanking AS ar2
        JOIN AnimeGenres AS ag ON ar2.anime_id = ag.anime_id
        JOIN Genres AS g ON ag.genre_id = g.genre_id

            WHERE g.genre_kor = #{genre}

)

    AND ar.rank_num > #{lastId}

    AND DATE(ar.rank_date) = #{rankDate}
ORDER BY ar.rank_num
LIMIT #{size}

SELECT a.anime_id AS animeId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, ar.rank_num AS rankNum FROM Anime AS a JOIN AnimeRanking AS ar ON a.anime_id = ar.anime_id WHERE ar.is_all_time = true AND ar.rank_type = 'all_time' AND a.anime_id IN ( SELECT DISTINCT(ar2.anime_id) FROM AnimeRanking AS ar2 JOIN AnimeGenres AS ag ON ar2.anime_id = ag.anime_id JOIN Genres AS g ON ag.genre_id = g.genre_id
WHERE g.genre_kor = #{genre}
) AND DATE(ar.rank_date) = #{rankDate} ORDER BY ar.rank_num

SELECT a.anime_id AS animeId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, ar.rank_num AS rankNum FROM Anime AS a JOIN AnimeRanking AS ar ON a.anime_id = ar.anime_id WHERE ar.is_all_time = true AND ar.rank_type = 'all_time' AND a.anime_id IN ( SELECT DISTINCT(ar2.anime_id) FROM AnimeRanking AS ar2 JOIN AnimeGenres AS ag ON ar2.anime_id = ag.anime_id JOIN Genres AS g ON ag.genre_id = g.genre_id
WHERE g.genre_kor = #{genre}
)
AND ar.rank_num > #{lastId}
AND DATE(ar.rank_date) = #{rankDate} ORDER BY ar.rank_num LIMIT #{size}
