mapper.select:
SELECT a.anime_id AS animeId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, ar.rank_num AS rankNum, g.genre_kor AS genreName FROM Anime AS a JOIN AnimeRanking AS ar ON a.anime_id = ar.anime_id JOIN Genres AS g ON ar.genre_id = g.genre_id WHERE ar.year = #{year} AND ar.season_int = #{season}
AND g.genre_kor = #{genre}
AND DATE(ar.rank_date) = #{rankDate} ORDER BY ar.rank_num

SELECT
    a.anime_id AS animeId,
    a.title_kor AS title,
    a.cover_image_url AS coverImageUrl,
    ar.rank_num AS rankNum,
    g.genre_kor AS genreName
FROM Anime AS a
JOIN AnimeRanking AS ar ON a.anime_id = ar.anime_id
JOIN Genres AS g ON ar.genre_id = g.genre_id
WHERE
        ar.year = #{year}
    AND
        ar.season_int = #{season}

    AND g.genre_kor = #{genre}


    AND ar.rank_num > #{lastId}

    AND DATE(ar.rank_date) = #{rankDate}
ORDER BY ar.rank_num
LIMIT #{size}

SELECT a.anime_id AS animeId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, ar.rank_num AS rankNum, g.genre_kor AS genreName FROM Anime AS a JOIN AnimeRanking AS ar ON a.anime_id = ar.anime_id JOIN Genres AS g ON ar.genre_id = g.genre_id WHERE ar.is_all_time = true
AND g.genre_kor = #{genre}
AND DATE(ar.rank_date) = #{rankDate} ORDER BY ar.rank_num

SELECT a.anime_id AS animeId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, ar.rank_num AS rankNum, g.genre_kor AS genreName FROM Anime AS a JOIN AnimeRanking AS ar ON a.anime_id = ar.anime_id JOIN Genres AS g ON ar.genre_id = g.genre_id WHERE ar.is_all_time = true
AND g.genre_kor = #{genre}
AND ar.rank_num > #{lastId}
AND DATE(ar.rank_date) = #{rankDate} ORDER BY ar.rank_num LIMIT #{size}
