mapper.select:
SELECT
    a.anime_id AS animeId,
    a.title_kor AS title,
    a.cover_image_url AS coverImageUrl,
    a.popularity AS popularity
FROM Anime AS a
JOIN AnimeGenres AS ag
ON a.anime_id = ag.anime_id
WHERE 1 = 1

    AND a.season_year = #{year}


    AND a.season_int = #{season}


    AND ag.genre_id = #{genreId}


    AND a.popularity < #{lastId}

ORDER BY a.popularity DESC
LIMIT #{size}

SELECT a.anime_id AS animeId, a.title_kor AS title, a.cover_image_url AS coverImageUrl, a.popularity AS popularity FROM Anime AS a JOIN AnimeGenres AS ag ON a.anime_id = ag.anime_id WHERE 1 = 1
AND ag.genre_id = #{genreId}
AND a.popularity < #{lastId}
ORDER BY a.popularity DESC LIMIT #{size}
