mapper.select:
SELECT a.anime_id AS animeId, an.title_kor AS titleKor, an.title_english AS titleEng, an.title_romaj AS titleRom, an.title_native AS titleNat, an.cover_image_url AS coverImageUrl, a.tag_count AS tagCount FROM ( SELECT at.anime_id, COUNT(*) AS tag_count FROM animetag at WHERE at.tag_id IN
#{tagId}
AND at.anime_id <> #{referenceAnimeId} GROUP BY at.anime_id ) AS a JOIN anime an ON an.anime_id = a.anime_id LEFT JOIN review r ON an.anime_id = r.anime_id AND r.user_id = #{userId} WHERE r.user_id IS NULL
AND ( a.tag_count < #{lastCount} OR (a.tag_count = #{lastCount} AND a.anime_id < #{lastId}) )
ORDER BY a.tag_count DESC, a.anime_id DESC LIMIT #{size}

SELECT at.tag_id AS tagId, SUM(r.rating * at.weight) AS score FROM review r JOIN animetag at ON at.anime_id = r.anime_id WHERE r.user_id = #{userId} AND r.anime_id IN
#{animeId}
GROUP BY at.tag_id ORDER BY score DESC

SELECT a.anime_id AS animeId, an.title_kor AS titleKor, an.title_english AS titleEng, an.title_romaj AS titleRom, an.title_native AS titleNat, an.cover_image_url AS coverImageUrl, a.tag_count AS tagCount FROM ( SELECT at.anime_id, COUNT(*) AS tag_count FROM animetag at WHERE at.tag_id IN
#{tagId}
GROUP BY at.anime_id ) AS a JOIN anime an ON an.anime_id = a.anime_id LEFT JOIN review r ON r.anime_id = an.anime_id AND r.user_id = #{userId} WHERE r.user_id IS NULL
AND ( a.tag_count < #{lastCount} OR (a.tag_count = #{lastCount} AND a.anime_id < #{lastId}) )
ORDER BY a.tag_count DESC, a.anime_id DESC LIMIT #{size}
