mapper.select:
SELECT a.anime_id AS animeId, a.title_kor AS titleKor, a.title_english AS titleEng, a.title_romaj AS titleRom, a.title_native AS titleNat, a.cover_image_url AS coverImageUrl FROM Anime a WHERE ( a.title_search LIKE CONCAT('%', #{query}, '%') )
AND a.anime_id < #{lastId}
ORDER BY a.anime_id DESC LIMIT #{size}

SELECT v.voiceactor_id AS personId, v.name_kor AS nameKor, v.name_eng AS nameEng, v.profile_image AS profileImage FROM VoiceActor v WHERE ( v.name_search LIKE CONCAT('%', #{query}, '%') )
AND v.voiceactor_id < #{lastId}
ORDER BY v.voiceactor_id DESC LIMIT #{size}

SELECT s.studio_id AS studioId,
       s.name_kor AS nameKor,
       s.name_eng AS nameEng
FROM Studio s
WHERE (
s.name_search LIKE CONCAT('%', #{query}, '%')
)

    AND s.studio_id < #{lastId}

ORDER BY s.studio_id DESC
LIMIT #{size}
