mapper.select:
SELECT COUNT(*)
FROM Anime a
WHERE a.title_kor LIKE #{queryPattern}
OR a.title_english LIKE #{queryPattern}
OR a.title_romaj LIKE #{queryPattern}

SELECT a.anime_id AS animeId,
       a.title_kor AS title,
       a.cover_image_url AS coverImageUrl
FROM Anime a
WHERE (
    a.title_kor LIKE #{queryPattern}
    OR a.title_english LIKE #{queryPattern}
    OR a.title_romaj LIKE #{queryPattern}
    )

    AND a.anime_id < #{lastId}

ORDER BY a.anime_id DESC
LIMIT #{size}

SELECT COUNT(*)
FROM Voiceactor v
WHERE v.name_kor LIKE #{queryPattern}
OR v.name_eng LIKE #{queryPattern}

SELECT v.voiceactor_id AS personId,
       v.name_kor AS name,
       v.profile_image AS profileImage
FROM VoiceActor v
WHERE (
    v.name_kor LIKE #{queryPattern}
    OR v.name_eng LIKE #{queryPattern}
    )

    AND v.voiceactor_id < #{lastId}

ORDER BY v.voiceactor_id DESC
LIMIT #{size}
