mapper.select:
SELECT EXISTS(
    SELECT 1
    FROM version
    WHERE platform = #{platform}
    AND major = #{major}
    AND patch = #{patch}
    AND minor = #{minor}
    AND type = #{type}
)

SELECT version_id AS versionId, platform AS platform, major AS major, minor AS minor, patch AS patch, important_yn AS importantYn, url AS url, type AS type, title AS title, updated_content AS updatedContent, updated_at AS updatedAt FROM Version WHERE platform = #{platform}
AND type = #{type}
ORDER BY version_id DESC LIMIT 50
